window.addEvent("domready",function(){$each($("show").getElementsByTagName("li"),function(d){var b=d.id.match(/^micro_(\d+)$/)[1]||false;if(b){var a=new Element("a",{rel:b,title:"Delte this entry","class":"delete",html:"[Del]",href:""});d.appendChild(a)}});$("show").addEvent("click",function(c){var b=c.target;if(b.hasClass("delete")&&b.nodeName.toLowerCase()&&b.rel&&c.stop()&&confirm("Are you sure, Master?")){var a=new Request({method:"get",url:"delete/"+b.rel+"/",onSuccess:function(f){if(f=="0"){return}var e=$("micro_"+b.rel);var d=new Fx.Morph(e,{duration:"long",transition:Fx.Transitions.Sine.easeOut,onComplete:function(){$("show").removeChild(e)}});d.start({opacity:0})}});a.send()}});if(Browser.Engine.trident){$$("#show li").addEvent("mouseenter",function(a){this.addClass("hover")});$$("#show li").addEvent("mouseleave",function(a){this.removeClass("hover")})}});
